mapper.update:
UPDATE score
LEFT JOIN course_selection
ON score.course_selection_id = course_selection.id
SET
    teacher_score = #{teacherScore}
WHERE score.student_id = #{studentId}
AND course_selection.course_id = #{course.id}

UPDATE score
LEFT JOIN course_selection
ON score.course_selection_id = course_selection.id
SET
company_score = #{companyScore}
WHERE score.student_id = #{studentId}
AND course_selection.course_id = #{course.id}
